import React, { useState } from 'react';

function TransactionForm({ userID }) {
  const [bicycleID, setBicycleID] = useState('');
  const [locationCode, setLocationCode] = useState('');
  const [timestamp, setTimestamp] = useState('');
  const [message, setMessage] = useState('');

  // Automatically fetch location code on load
  React.useEffect(() => {
    const fetchLocation = async () => {
      if ('geolocation' in navigator) {
        navigator.geolocation.getCurrentPosition(
          (position) => {
            const { latitude, longitude } = position.coords;
            // Example of how to set location code based on coordinates
            setLocationCode(`${latitude.toFixed(4)},${longitude.toFixed(4)}`);
          },
          (error) => {
            console.error('Error fetching location:', error);
            setLocationCode('UNKNOWN');
          }
        );
      } else {
        setLocationCode('NOT_SUPPORTED');
      }
    };

    fetchLocation();
    setTimestamp(new Date().toISOString());
  }, []);

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    const transactionData = {
      ifBorrowed: 'tak', // Fixed for borrow action
      timestamp,
      locationCode,
      customer: {
        customerID: userID,
      },
      bicycle: {
        bicycleID: Number(bicycleID),
      },
    };

    try {
      const response = await fetch('/api/transactions', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(transactionData),
      });

      if (response.ok) {
        const result = await response.json();
        setMessage(`Transaction successful! Transaction ID: ${result.transactionID}`);
      } else {
        const error = await response.json();
        setMessage(`Error: ${error.message}`);
      }
    } catch (error) {
      console.error('Error submitting transaction:', error);
      setMessage('Failed to submit transaction.');
    }
  };

  return (
    <div>
      <h1>Borrow Bicycle</h1>
      <form onSubmit={handleSubmit}>
        <label>
          Bicycle Number:
          <input
            type="number"
            value={bicycleID}
            onChange={(e) => setBicycleID(e.target.value)}
            required
          />
        </label>
        <br />
        <button type="submit">Submit</button>
      </form>
      {message && <p>{message}</p>}
    </div>
  );
}

export default TransactionForm;
