import React, { useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';

const TransactionRegistration = () => {
  const navigate = useNavigate();
  const location = useLocation();

  // State for the form inputs
  const [transaction, setTransaction] = useState({
    ifBorrowed: 'yes', // Default value assuming the action is borrowing
    timestamp: new Date().toISOString(),
    locationCode: '',
    customerId: '',
    bicycleId: location.state?.bicycleId || '', // Retrieve bicycleId from navigation state
  });

  // Handle changes to the form inputs
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setTransaction({ ...transaction, [name]: value });
  };

  // Placeholder for submit action
  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Form data:', transaction);
    // Integrate with backend API when ready
  };

  return (
    <div style={{ maxWidth: '600px', margin: '0 auto', padding: '20px' }}>
      <h2>Transaction Registration</h2>
      <form onSubmit={handleSubmit}>
        <div style={{ marginBottom: '10px' }}>
          <label htmlFor="ifBorrowed">Transaction Type:</label>
          <select
            id="ifBorrowed"
            name="ifBorrowed"
            value={transaction.ifBorrowed}
            onChange={handleInputChange}
          >
            <option value="yes">Borrow</option>
            <option value="no">Return</option>
          </select>
        </div>

        <div style={{ marginBottom: '10px' }}>
          <label htmlFor="locationCode">Location Code:</label>
          <input
            type="text"
            id="locationCode"
            name="locationCode"
            value={transaction.locationCode}
            onChange={handleInputChange}
            placeholder="Enter location code"
          />
        </div>

        <div style={{ marginBottom: '10px' }}>
          <label htmlFor="customerId">Customer ID:</label>
          <input
            type="number"
            id="customerId"
            name="customerId"
            value={transaction.customerId}
            onChange={handleInputChange}
            placeholder="Enter customer ID"
          />
        </div>

        <div style={{ marginBottom: '10px' }}>
          <label htmlFor="bicycleId">Bicycle ID:</label>
          <input
            type="number"
            id="bicycleId"
            name="bicycleId"
            value={transaction.bicycleId}
            readOnly // Prevent manual modification
          />
        </div>

        <div style={{ marginBottom: '10px' }}>
          <label htmlFor="timestamp">Timestamp:</label>
          <input
            type="text"
            id="timestamp"
            name="timestamp"
            value={transaction.timestamp}
            readOnly // Prevent manual modification
          />
        </div>

        <button type="submit" disabled>
          Submit (Coming Soon)
        </button>
      </form>
    </div>
  );
};

export default TransactionRegistration;
