import React, { useState, useEffect } from 'react';
import axios from 'axios';

const TransactionRegistration = () => {
  const [customers, setCustomers] = useState([]);
  const [bicycles, setBicycles] = useState([]);
  const [selectedCustomer, setSelectedCustomer] = useState('');
  const [selectedBicycle, setSelectedBicycle] = useState('');
  const [location, setLocation] = useState('');
  const [status, setStatus] = useState('Borrowed');

  useEffect(() => {
    axios.get('http://localhost:8081/customers').then((res) => setCustomers(res.data));
    axios.get('http://localhost:8081/bicycles').then((res) => setBicycles(res.data));
  }, []);

  const handleRegister = () => {
    axios.post('http://localhost:8081/transactions', {
      customerID: selectedCustomer,
      bicycleID: selectedBicycle,
      status,
      location,
      timestamp: new Date().toISOString(),
    })
      .then(() => alert('Transaction registered successfully!'))
      .catch((error) => console.error('Error registering transaction:', error));
  };

  return (
    <div className="card">
      <h2>Transaction Registration</h2>
      <select value={selectedCustomer} onChange={(e) => setSelectedCustomer(e.target.value)}>
        <option value="">Select Customer</option>
        {customers.map((customer) => (
          <option key={customer.customerID} value={customer.customerID}>
            {customer.firstName} {customer.lastName}
          </option>
        ))}
      </select>
      <select value={selectedBicycle} onChange={(e) => setSelectedBicycle(e.target.value)}>
        <option value="">Select Bicycle</option>
        {bicycles.map((bicycle) => (
          <option key={bicycle.bicycleID} value={bicycle.bicycleID}>
            {bicycle.shortDescription}
          </option>
        ))}
      </select>
      <input type="text" placeholder="Location" value={location} onChange={(e) => setLocation(e.target.value)} />
      <select value={status} onChange={(e) => setStatus(e.target.value)}>
        <option value="Borrowed">Borrowed</option>
        <option value="Returned">Returned</option>
      </select>
      <button onClick={handleRegister}>Register Transaction</button>
    </div>
  );
};

export default TransactionRegistration;
